import React, { useState } from 'react'
import { connect } from 'react-redux'
import UserContainer from './UserContainer'
// import plusIcon from '../../static/svg/plusB.svg'
// import userIcon from '../../static/svg/userSB.svg'
// import bellIconB from '../../static/svg/bellB.svg'
// import heartIconB from '../../static/svg/heartB.svg'
// import moreIconB from '../../static/svg/moreSB.svg'
// import bellIconW from '../../static/svg/bellW.svg'
// import heartIconW from '../../static/svg/heartW.svg'
// import moreIconW from '../../static/svg/moreW.svg'
// import logoIcon from '../../static/svg/logo/logoB.svg'
// import hamburgerIcon from '../../static/svg/hamburger.svg'
// import searchIconW from '../../static/svg/searchW.svg'
// import searchIconB from '../../static/svg/searchSB.svg'
// import exitIcon from '../../static/svg/exitB.svg'
import NB from '../../styles/navBar.module.css'
import { Link, withRouter } from 'react-router-dom'
import SkillSugg from '../text-field/SkillSugg'
import UserNotifList from '../user/utilities/UserNotifList'
import UserSP from '../user/utilities/UserSP'
import SignIn from '../user/form/SignIn'
import Register from '../user/form/Register'
import { clearToken } from '../../redux/reducers/token'
import { resetAlert } from '../../redux/reducers/alertNotif'
import {bindActionCreators} from 'redux'

let NavBar = (props) => {
    const [utilityShown, setUtilityShown] = useState(null)
    const [menuItemShown, setMenuItemShown] = useState(null)

    const changeUtility = (name) => {
        setMenuItemShown(null)
        if (name === utilityShown) setUtilityShown(null)
        else setUtilityShown(name)
    }

    const changeMenuItem = (item) => {
        if (item === menuItemShown) setMenuItemShown(null)
        else setMenuItemShown(item)
    }

    const linkToPush = `/browse/${encodeURIComponent(props.query)}`

    const enterQuery = () => {
        props.history.push(linkToPush)
    }

    const handleKeyPress = (event) => {
        if (event.key === 'Enter'){
            enterQuery()
        }
    }

    const onSuggestionClicked = (skill) => {
        const splitQuery = props.query.split(' ')
        const newQuery = splitQuery.length === 1 ? `${skill} ` : `${splitQuery.slice(0, -1).join(' ')} ${skill} `
        props.onQueryChange({target: {value: newQuery}})
    }

    const handleLogout = () => {
        localStorage.clear()
        props.clearToken()
    }

    const menuStyle = menuItemShown ? {borderBottomColor: '#282828'} : null

    const pendingNotifStats = props.currentUser && props.currentUser.pendingNotifications > 0 ?
        [NB.PNBubble, props.currentUser.pendingNotifications] : [null, null]

    const ShowInSubNav = () => {
        const moreItemButton = menuItemShown === 'more' ?
            <img src="/svg/moreSB.svg" onClick={() => changeMenuItem('more')} className={NB.menuItemSelected} alt="more" />
            :
            <img src="/svg/moreSB.svg" onClick={() => changeMenuItem('more')} className={NB.menuItem} alt="more" />
        if (utilityShown === 'menu') {
            if (props.token) {
                // const allItems = [
                //     {name: 'notifications', iconB: bellIconB, iconW: bellIconW},
                //     {name: 'following', iconB: heartIconB, iconW: heartIconW},
                // ]
                const notifItemButton = menuItemShown === 'notifications' ?
                    <div onClick={() => changeMenuItem('notifications')} className={`${NB.menuItemSelected} ${NB.PNContainer}`}>
                        <img src={bellIconW}  className={NB.notificationItem} alt={'notifications'} key={`UO${'notifications'}`} />
                        <div className={pendingNotifStats[0]} >{pendingNotifStats[1]}</div>
                    </div>
                    :
                    <div onClick={() => changeMenuItem('notifications')} className={`${NB.menuItem} ${NB.PNContainer}`}>
                        <img src={bellIconB} className={NB.notificationItem} alt={'notifications'} key={`UO${'notifications'}`} />
                        <div className={pendingNotifStats[0]} >{pendingNotifStats[1]}</div>
                    </div>

                const followingItemButton = menuItemShown === 'following' ? 
                    <img src={heartIconW} onClick={() => changeMenuItem('following')} className={NB.menuItemSelected} alt={'following'} key={`UO${'following'}`} />
                    :
                    <img src={heartIconB} onClick={() => changeMenuItem('following')} className={NB.menuItem} alt={'following'} key={`UO${'following'}`} />


                return (
                    <div className={NB.navBarSubContainerM} style={menuStyle}>
                        <DIV to="/post-form/" onClick={() => changeMenuItem(null)} className={NB.menuItem} style={{padding: 0, margin: 0}}><img src={plusIcon} className={NB.menu-item} alt="add project" /></DIV>
                        <DIV to={`/user/${props.currentUser.username}`} onClick={() => changeMenuItem(null)} className={NB.menuItem} style={{padding: 0, margin: 0}}><img src={userIcon} className="menu-item" alt="user page" /></DIV>
                        {notifItemButton}
                        {followingItemButton}
                        <img src={exitIcon} onClick={() => handleLogout()} className={NB.menu-item} alt="log out" />
                        {moreItemButton}
                    </div>
                )
            }
            const allItems = ['sign in', 'register']
            const modalItemButtons = allItems.map(i => menuItemShown === i ? 
                <h3 onClick={() => changeMenuItem(i)} className={NB.menu-item-selected} key={`MI${i}`} >{i}</h3>
                :
                <h3 onClick={() => changeMenuItem(i)} className={NB.menu-item} key={`MI${i}`} >{i}</h3>
            )
            return (
                <div className={`${NB.nav-bar-sub-container-m} ${NB.login-bar}`} style={menuStyle}>
                    {modalItemButtons}
                    {moreItemButton}
                </div>
            )

        }
        return null
    }


    const MenuItemToShow = () => {
        if (menuItemShown === 'sign in') {
            return (
                <div className={NB.menuModalContainer}>
                    <SignIn noBG={true} changeMenuItem={changeMenuItem} />
                </div>
            )
        }
        if (menuItemShown === 'register') {
            return (
                <div className={NB.menuModalContainer}>
                    <Register noBG={true} changeMenuItem={changeMenuItem} />
                </div>
            )
        }
        if (menuItemShown === 'notifications') {
            return (
                <div className={NB.menuModalContainer}>
                    <h2>notifications</h2>
                    <UserNotifList />
                </div>
            )
        }
        if (menuItemShown === 'following') {
            return (
                <div className={NB.menuModalContainer}>
                    <h2>saved posts</h2>
                    <UserSP />
                </div>
            )
        }
        if (menuItemShown === 'more') {
            return (
                <div className={NB.menuModalContainer}>
                    <div to="/contact/" className="neutralize-link"><h1 className={NB.more-link}>contact us</h1></div>
                </div>
            )
        }
        return null
    }
    const showWhenSB = utilityShown === 'search' ? {display: 'block'} : {display: 'none'}
    const alertBarStyle = props.alertNotif.color ?
        {backgroundColor: props.alertNotif.color, color: props.alertNotif.textColor}
        :
        {display: 'none', backgroundColor: props.alertNotif.color, color: props.alertNotif.textColor}



    return (
        <div>
            <h2>NavBar</h2>
            <div className={`${NB.nav-container} HWM`}>
                <div className={NB.nav-bar-container}>
                    <div to="/" className={NB.icon-container} >
                        <img src="/svg/logo/logoB.svg" alt="icon" className={NB.nav-bar-icon} />
                    </div>
                    <div className="search-bar">
                        <div to={linkToPush} className={`neutralize-link ${NB.enter-search} ${NB.search-hover}`} onClick={() => enterQuery()}>
                            <img src="/svg/searchW.svg" className={NB.search-icon} alt="search" />
                        </div>
                        <div className={NB.search-hr} style={{backgroundColor: 'white'}} />
                        <input value={props.query} className={NB.search-input} type="text" onChange={(e) => props.onQueryChange(e)} onKeyPress={(e) => handleKeyPress(e)} placeholder="search or enter nothing for all results" />
                        <div className={NB.SB-separator} />
                        <SkillSugg query={props.query} onSuggestionClicked={onSuggestionClicked} />
                    </div>
                    <UserContainer />
                </div>
                <div className={NB.alert-bar-container} style={alertBarStyle}>
                    <div className={NB.message}>{props.alertNotif.message}</div>
                    <h2 onClick={() => props.resetAlert()} className={NB.close}>x</h2>
                </div>
            </div>
            <div className={`${NB.nav-container-m} SWM`}>
                <div className={NB.nav-bar-container-m}>
                    <div to="/" className={`${NB.NB-brand} neutralize-link`}>
                        <img src="/svg/logo/logoB.svg" className={NB.NB-logo} alt="logo" />
                        <h2>Unilous</h2>
                    </div>
                    <div className={NB.NB-primary-utilities}>
                        <img src="/svg/searchSB.svg" onClick={() => changeUtility('search')} className={NB.PU-search} alt="search" />
                        <div onClick={() => changeUtility('menu')} className={`${NB.PU-hamburger-container} ${NB.PN-container}`}>
                            <img src="/svg/hamburger.svg" className={NB.PU-hamburger} alt="menu" />
                            <div className={`${pendingNotifStats[0]} ${NB.PN-bubble-hamburger}`}>{pendingNotifStats[1]}</div>
                        </div>
                    </div>
                </div>
                <div onClick={() => props.resetAlert()} className={NB.alert-bar-container} style={alertBarStyle}>
                    <div className={NB.message}>{props.alertNotif.message}</div>
                    <h2 className={NB.close}>x</h2>
                </div>
                <div className={NB.nav-bar-sub-container} style={showWhenSB}>
                    <div className={NB.search-bar}>
                        <div to={linkToPush} className={`neutralize-link ${NB.enter-search} ${NB.search-hover}`} onClick={() => enterQuery()}>
                            <img src="/svg/searchW.svg" className={NB.search-icon} alt="search" />
                        </div>
                        <div className={NB.search-hr} style={{backgroundColor: 'white', height: '0'}} />
                        <input value={props.query} className={NB.search-input} type="text" onChange={(e) => props.onQueryChange(e)} onKeyPress={(e) => handleKeyPress(e)} placeholder="search or enter nothing for all results" />
                        <SkillSugg query={props.query} onSuggestionClicked={onSuggestionClicked} />
                    </div>
                </div>
                <ShowInSubNav />
                <MenuItemToShow />
            </div>
        </div>
    )
}

