import React, { useState, useEffect } from 'react'
import { connect } from 'react-redux'
import { useQuery } from '@apollo/react-hooks'
import { FIND_USER } from '../../../src/schemas/queries'
import { setCurrentUser } from '../../../redux/reducers/currentUser'
import { clearToken } from '../../../redux/reducers/token'
import UserNotifList from '../utilities/UserNotifList'
import UserSP from '../utilities/UserSP'
import UU from '../../../styles/user/userUtilities.module.css'
import UR from '../../../styles/user/user.module.css'
import {bindActionCreators} from 'redux'

const Logged = (props) => {
    const [showUtilities, setShowUtilities] = useState(true)
    const [utilityShown, setUtilityShown] = useState('')

    const currentUserUN = localStorage.getItem('username') ? localStorage.getItem('username') : 'fakeUser'
    const userQuery = useQuery(FIND_USER, {
        variables: {username: currentUserUN}
    })
    const userQueryConditions = () => {
        const query = userQuery.data
        const reducer = props.currentUser
        if (!query) { return null }
        if (!query.findUser) { return null }
        if (reducer) {
            if (query.findUser.username === reducer.username) { return null }
        }
        return query.findUser
    }
    const userResult = userQueryConditions()

    useEffect(() => {
        if (userResult) {
            props.setCurrentUser(userResult)
        }
    }, [props, props.currentUser, userResult])

    if (!props.currentUser) {
        return (
            <div className={`${UR.user-container} ${UR.LGD-user-container}`}>
                <div className={UR.user-options-splitter}>
                    <div className={UR.user-icon-container}>
                        {/* <img src="/svg/userW.svg" className="user-icon" alt="user" /> */}
                    </div>
                </div>
                <div className={UR.user-banner}>
                </div>
            </div>
        )
    }
    
    const logout = () => {
        localStorage.clear()
        props.clearToken()
    }

    const changeUtilityShown = (changeTo) => {
        if (changeTo === utilityShown) {
            setUtilityShown(null)
        }
        else {
            setUtilityShown(changeTo)
        }
        setShowUtilities(true)
    }
    const changeShowUtilities = (changeTo) => {
        if (!changeTo) {
            setShowUtilities(changeTo)
            setUtilityShown(null)
        }
        else {
            setShowUtilities(changeTo)
        }
    }
    const usernameIcon = props.currentUser.username.substr(0, 1).toUpperCase()

    const iconToShow = showUtilities ?
        <div to={`/user/${encodeURIComponent(props.currentUser.username)}`} className="user-icon-container UICH neutralize-link">
            <h1 className={`${UR.user-username-icon} ${UR.user-icon-hover}`}>{usernameIcon}</h1>
        </div>
        :
        <div to={`/user/${encodeURIComponent(props.currentUser.username)}`} className="user-icon-container UICH">
            <img src="/svg/userW.svg" className={`${UR.user-icon} ${UR.user-icon-hover}`} alt="user" />
        </div>

    const basicStyles = {user: {className: '', icon: "/svg/userSB.svg"}, notifications: {className: '', icon: "/svg/bellB.svg"}, following: {className: '', icon: "/svg/heartB.svg"}}
    if (utilityShown === 'user') { basicStyles.user = {className: UR.UUO-selected, icon: "/svg/userW.svg"}}
    if (utilityShown === 'notifications') { basicStyles.notifications = {className: UR.UUO-selected, icon: "/svg/bellW.svg"}}
    if (utilityShown === 'following') { basicStyles.following = {className: UR.UUO-selected, icon: "/svg/heartW.svg"}}
    
    const pendingNotifStats = props.currentUser && props.currentUser.pendingNotifications > 0 ?
        [UR.PN-bubble, props.currentUser.pendingNotifications] : [null, null]

    const showInBanner = showUtilities ?
        <React.Fragment>
            <div to="/post-form/" className={`${UR.UU-add-post} ${UR.user-utility-option} neutralize-link`} style={{gridColumn: '1/3'}}>
                <img src="/svg/plusB.svg" className={UR.UUAP-icon} alt="add post"/>
                <h4 className={UR.UUAP-text}>add post</h4>
            </div>
            <div to={`/user/${props.currentUser.username}`} className={`user-utility-option ${basicStyles.user.className}`}>
                <img src={basicStyles.user.icon} className="UU-icon" alt="user" />
            </div>
            <div onClick={() => changeUtilityShown('notifications')} className={`user-utility-option PN-container ${basicStyles.notifications.className}`}>
                <img src={basicStyles.notifications.icon} className="UU-icon" alt="notifications" />
                <div className={pendingNotifStats[0]}>{pendingNotifStats[1]}</div>
            </div>
            <div onClick={() => changeUtilityShown('following')} className={`${UR.user-utility-option} ${basicStyles.following.className}`}>
                <img src={basicStyles.following.icon} className={UR.UU-icon} alt="follow" />
            </div>
            <div onClick={() => logout()} className={`${UR.user-utility-option} ${UR.UU-logout}`}>
                <img src="/svg/exitB.svg" className={UR.UU-logout-icon} alt="log out" />
            </div>
        </React.Fragment>
        :
        <div onClick={() => changeShowUtilities(!showUtilities)} className={`${UR.user-username-container} ${UR.SUH}`}><h2 className="user-username">{props.currentUser.username}</h2></div>

    const showBubble = showUtilities ?
        <div />
        :
        <div onClick={() => changeUtilityShown('notifications')} className={UR.user-notification-bubble}>4</div>


    const showUtilityContent = utilityShown ? 
        {display: 'block'} : {display: 'none'}

    const utilityContent = () => {
        // if (utilityShown === 'user') {
        //     return <div><UserInfo /></div>
        // }
        if (utilityShown === 'notifications') {
            return <UserNotifList />
        }
        if (utilityShown === 'following') {
            return <UserSP />
        }
        return null
    }
    
    return (
        <div className={`${UR.user-container} ${UR.LGD-user-container}`}>
            <div className={UR.user-options-splitter}>
                {iconToShow}
            </div>
            <div className={UR.user-banner}>
                {showInBanner}
            </div>
            {showBubble}
            <div className={UR.UU-utilities-splitter} style={showUtilityContent}/>
            <div className={UR.UU-content-container} style={showUtilityContent}>
                <h3>{utilityShown}</h3>
                {utilityContent()}
            </div>
        </div>
    )
}


const mapStateToProps = (state) => {
	return {
        currentUser: state.currentUser
	}
}

const mapDispatchToProps = (dispatch) => {
    return {
        clearToken: bindActionCreators(clearToken, dispatch)
    }
}

export default connect(
    mapStateToProps,
    { clearToken, setCurrentUser }
)(Logged)
