import React from 'react'
import { connect } from 'react-redux'
import Layout from '../../components/Layout'
import Head from 'next/head'
import B from '../../styles/blog.module.css'
import marked from 'marked'

const RtN = () => {
    const blog = require('../../blogs/react_to_nextjs.md')
    const blogHTML = marked(blog.text())
    return (
        <Layout>
            <Head>
                <title>Contact | Unilous</title>
                <meta name="description" content="Contact the Unilous team" key="description"/>
            </Head>
            <div className="navbar-height" />
            <div className={B.container}>
                <h1 className={B.title}>Converting a React application with Redux and Apollo to Next.JS</h1>
                <img className={B.imgTitle} src="/svg/events/usaB.svg" />
                <p className={B.text}>
                    If you have clicked on this blog odds are that you have heard of <a href="https://nextjs.org/">Next.JS</a>, if not allow me to introduce it. 
                    Next.JS is a <a href="https://reactjs.org/">React</a> framework that is best known for its server side rendering capablities which allows for better search engine optimization (SEO). 
                    In short the reason for which we among other React developers switch to Next.JS is to aliviate the hastle of SEO in the most effective manner.
                </p>
                <p className={B.text}>
                    Although Next.JS is already built on the shoulders of React, there are still many changes which need to happen before gettin the application up and running.
                    For this guide I will not only show how to transform a React application to a Next.JS application, but a react application built with the centralized state management: <a href="https://redux.js.org/">Redux</a>, and the GraphQL API: <a href="https://www.apollographql.com/">Apollo</a>. 
                </p>
                <h2 className={B.title}>1. Setting up your node environment</h2>
                <p className={B.text}>
                    First you must set up the proper node packages for Next.JS to be able to run the application.
                </p>
                <article>
                    <div dangerouslySetInnerHTML={{ __html: blogHTML }} />
                </article>
            </div>
        </Layout>
    )
}

const mapStateToProps = (state) => {
	return {
        // currentUser: state.currentUser,
	}
}

const mapDispatchToProps = (dispatch) => {
    return {
        // setUserDD: bindActionCreators(setUserDD, dispatch),
    }
}

export default connect(
    mapStateToProps,
    mapDispatchToProps
)(RtN)
